mapper.select:
select * from t_category where 1 = 1
and categoryName like concat('%', #{category.categoryName}, '%')
order by sortNum desc limit #{start},#{limit}

mapper.update:
update t_category
set
categoryName = #{categoryName} ,aliasName =
#{aliasName} , iconClass = #{iconClass}, sortNum = #{sortNum}
where id = #{id}
